catalog_script_client.script:
function onLoad() {
	//Type appropriate comment here, and begin script below

	var $ = this.jQuery;

	var fields = ["department", "requested_by", "submission_type", "title", "business_owner_title", "product_owner_title", "state_entity", "business_program_name", "enterprise_architect_title", "financial_benefit", "better_services", "executive_summary_of_the_business_problem_or_opportunity", "project_management_risk_score", "business_complexity_score", "anticipate_additional_resources_through_a_budget_action_to_complete_planning"];


	setTimeout(function() {

		for (var i=0; i < fields.length; i++) {

			var $button = $("<button id='la_" + fields[i] + "'" + "style='margin-bottom:10px;margin-top:5px;color:#4f52bd;' class='btn btn-icon' >" +
							"<i class='fa fa-plus fa-lg'></i></button>");

			var field = "#" + fields[i];
			var fieldElement = $(field);
			var fieldtitle = fieldElement.closest('div.row').prev();
			fieldtitle.append($button);

			var $row = fieldElement.closest('div.row').attr('id', 'la_' + fields[i]);

			$button.click(function(event) {
				var elementID = event.currentTarget.id;
				$('div#' + elementID).toggle();
				$('button#' + elementID).find('i').toggleClass("fa-plus fa-minus");
			});

			if (i == 0) {
				$('button#la_' + fields[i]).find('i').toggleClass("fa-plus fa-minus");
				continue;
			}

			$row.hide();

		}

	}, 0);
}
