sys_alias_templates.dynamic_data_schema:
{
  "connection_fields": [{
	"name": "connection.name",
	"label": "Connection Name",
	"type": "text",
	"defaultValue": "City S2P Spoke Connection",
	"hint": "Display name for the Connection", 
        "mandatory":true
    }, 
    {
      "name": "connection.connection_url",
      "label": "Connection URL",
      "type": "text",
      "defaultValue": "https://sb.cityinnovate.com",
      "hint": "Connection URL for provider", 
        "mandatory":true
    }
  ], "credential_fields": [
    {
      "name": "credential.user_name",
      "label": "User Name",
      "type": "text",
      "defaultValue": "",
      "hint": "Username is the Token ID received via email during registration", 
        "mandatory":true
    }, 
    {
      "name": "credential.password",
      "label": "Password",
      "type": "password",
      "defaultValue": "",
      "hint": "Password is the Secret  received via email during registration", 
        "mandatory":true
    }
  ]
}

sys_alias_templates.on_edit_script:
(function execute(aliasId, connectionSysId, jsonDefaultData, jsonDynamicData) {
   // aliasId: sys_id of the alias record
   // connectionSysId: sys_id of the connection record created by using the default and dynamic data
   // jsonDefaultData (String): The default JSON template data from template table
   // e.g. var jsonDefaultParsed = JSON.parse(jsonDefaultData);
   //      gs.info("default connection url = " + jsonDefaultParsed["connection"]["connection_url"]);
   // jsonDynamicData (String): User input based on the key/values defined in "Dynamic Data"
   // e.g. var jsonDynamicParsed = JSON.parse(jsonDynamicData);
   //      gs.info("dynamic connection url = " + jsonDynamicParsed["connection.connection_url"]);
})(aliasId, connectionSysId, jsonDefaultData, jsonDynamicData);

