sys_script_include.script:
var getChoicesAJAX = Class.create();
getChoicesAJAX.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getChoices: function() {
        var elementsparm = String(this.getParameter('sysparm_elements'));
        var elements = elementsparm.split(",");
        choicesObj = {};

        for (var i = 0; i < elements.length; i++) {
            var name = elements[i];
            var choiceRecord = new GlideRecord('x_citin_s2p_spoke_document_builder_constants');
            choiceRecord.addEncodedQuery('type=' + name);
            choiceRecord.query();

            choicesObj[name] = {};
            var count = 0;

            while (choiceRecord.next()) {
                choicesObj[name][count] = {
                    displayValue: choiceRecord.name.toString(),
                    value: choiceRecord.value.toString()
                }
                count++;
            }
        }

        var result = JSON.stringify(choicesObj);
        return result;

    },
    type: 'getChoicesAJAX'
});
