sys_ui_action.script:
//Client-side 'onclick' function

function createCIProject() {
        popupDispList();
        return true;
}
// function clearMessages(){
// 	setTimeout(function() {
// 			g_form.clearMessages();
// 		}, 5000); 
// }
function checkAssignedUserAndProceed() {
	var fields = ['number', 'assigned_to', 'short_description', 'x_citin_s2p_spoke_ci_initial_workflow'];
    var fieldLabels = {
        'number': 'Number',
        'assigned_to': 'Assigned To',
        'short_description': 'Short Description',
        'x_citin_s2p_spoke_ci_initial_workflow': 'CI Initial Workflow'
    };

    var missing_fields = [];

    for (var i = 0; i < fields.length; i++) {
        var fieldName = fields[i];
        var fieldValue = g_form.getValue(fieldName);
        var fieldLabel = fieldLabels[fieldName];

        if (fieldValue === "" || fieldValue === null) {
            missing_fields.push(fieldLabel);
        }
    }

    if (missing_fields.length > 0) {
        var errorMessage = "The following mandatory fields are missing: " + missing_fields.join(", ") + ". Please fill these fields out before moving forward.";
        g_form.addErrorMessage(errorMessage);
// 		clearMessages();
        return;
	}
	if (g_form.modified) {
        g_form.addErrorMessage("Values on the form have changed.\nPlease save the form before continuing.");
// 		clearMessages();
        return;
    }
	var sourcingRequestId = g_form.getUniqueValue();
    var ga = new GlideAjax('citySourcingRequestFunctions');
    ga.addParam('sysparm_name', 'hasValueInField');
    ga.addParam('sysparm_table', 'sn_shop_sourcing_activity'); 
    ga.addParam('sysparm_recordId', sourcingRequestId);
    ga.addParam('sysparm_userField', 'assigned_to'); 
    ga.addParam('sysparm_fieldToCheck', 'x_citin_s2p_spoke_ci_procurement_official_id');
    ga.getXML(handleResponse);
}
function handleResponse(response) {
	var result = response.responseXML.documentElement.getAttribute('answer');
    if(result == "true"){
		createCIProject();		
	}else {
		g_form.addErrorMessage("Select a user with a valid CI Project ID in the Assigned to field. Use the filter option to display users with the Project ID.");
// 		clearMessages();
		return;
	}
}

function popupDispList() {
    //Initialize the GlideDialogWindow	
    var gdw = new GlideDialogWindow('x_citin_s2p_spoke_create_ci_project');
    gdw.setTitle('Create CI Project');
    gdw.setSize(420, 700);
    gdw.setPreference('sr_sys_id', g_form.getUniqueValue());
    //Open the dialog window
    gdw.render();
}
